    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_word_view);
        _btnBack=(Button)findViewById(R.id.btn_WordBack);
        _btnFamily=(Button)findViewById(R.id.btn_WordFamily);
        _btnYoutube=(Button)findViewById(R.id.btn_WordYoutube);
        _text=(TextView)findViewById(R.id.txt_word);

        _btnBack.setOnClickListener(btnListener);
        _btnFamily.setOnClickListener(btnListener);
        _btnYoutube.setOnClickListener(btnListener);
        _text.setOnTouchListener(new ZoomListenter());
        Intent intent=this.getIntent();
        Bundle bundle=intent.getExtras();
        _albumName=bundle.getString("AlbumName");
        _location=bundle.getInt("Location");

        choiceAlbum();

        _webViewYoutube =(WebView)findViewById(R.id.word_youtube);
        _webViewYoutube.setVisibility(View.GONE);
        _scrWord=(ScrollView)findViewById(R.id.scrWord);
    }

    @Override
    protected void onDestroy() {
        super.onDestroy();

        _webViewYoutube.clearHistory();
        _webViewYoutube.clearCache(true);
        _webViewYoutube.destroy();
        _webViewYoutube = null;
    }

