    private Button.OnClickListener btnListener=new Button.OnClickListener(){
        public void onClick(View v){
            switch (v.getId()){
                case R.id.btn_WordBack:
                    finish();
                    break;
                case R.id.btn_WordFamily:
                    Intent family=new Intent();
                    family.setClass(WordView.this,FamilyArticle.class);
                    startActivity(family);
                    break;
                case R.id.btn_WordYoutube:
                    if(_webViewState=="close"){
                        _scrWord.setVisibility(View.GONE);
                        _webViewYoutube.setVisibility(View.VISIBLE);
                        WebSettings webSettings= _webViewYoutube.getSettings();
                        webSettings.setJavaScriptEnabled(true);
                        //webSettings.setSupportZoom(true);
                        //webSettings.setBuiltInZoomControls(true);
                        webSettings.setDomStorageEnabled(true);
                        _webViewYoutube.setWebViewClient(new WebViewClient());
                        _webViewYoutube.loadUrl(_url);
                        _btnYoutube.setText("隱藏Youtube");
                        _webViewState="play";
                    }
                    else if(_webViewState=="play"){
                        _scrWord.setVisibility(View.VISIBLE);
                        _webViewYoutube.setVisibility(View.GONE);
                        _webViewState="hidden";
                        _btnYoutube.setText("顯示Youtube");
                    }
                    else if(_webViewState=="hidden"){
                        _scrWord.setVisibility(View.GONE);
                        _webViewYoutube.setVisibility(View.VISIBLE);
                        _btnYoutube.setText("隱藏Youtube");
                        _webViewState="play";
                    }
                    break;
            }
        }
    };

